        console.log("ðŸ”µ API YanÄ±t Kodu:", response.status);

        if (!response.ok) {
            throw new Error(`HTTP HatasÄ±! Durum Kodu: ${response.status}`);
        }

        const data = await response.json();
        console.log("âœ… JSON Olarak Ä°ÅŸlenen Veri:", data);

        if (!Array.isArray(data)) {
            console.error("âš ï¸ Beklenmeyen API yanÄ±tÄ±:", data);
            return [];
        }

        return data.map(video => ({
            title: video.title,
            url: video.url,
            thumbnail: video.thumbnail || ""  // EÄŸer thumbnail boÅŸsa, hata vermesin diye.
        }));
    } catch (error) {
        console.error("âŒ YouTube verileri Ã§ekilemedi:", error);
        return [];
    }
};









export const fetchInstagramData = async () => {
    const response = await fetch("API_URL_INSTAGRAM");
    const data = await response.json();
    return data.map(post => ({
        title: post.caption,
        url: post.permalink,
        thumbnail: post.media_url
    }));
};
