export const fetchRedditData = async () => {
    const response = await fetch("https://www.reddit.com/r/popular/top.json?limit=10");
    const data = await response.json();
    return data.data.children.map(post => ({
        title: post.data.title,
        url: post.data.url,
        thumbnail: post.data.thumbnail !== "self" ? post.data.thumbnail : null
    }));
};


export const fetchTwitterData = async () => {
    const response = await fetch("API_URL_TWITTER");
    const data = await response.json();
    return data.map(tweet => ({
        title: tweet.text,
        url: `https://twitter.com/user/status/${tweet.id}`,
        thumbnail: tweet.media ? tweet.media[0].url : null
    }));
};

export const fetchYouTubeData = async () => {
    try {
        const response = await fetch("http://localhost:8080/api/youtube/trends");

        console.log("📡 API Yanıtı:", response); // Yanıtı görmek için

        if (!response.ok) {
            throw new Error(`HTTP Hatası! Durum Kodu: ${response.status}`);
        }

        const textData = await response.text();  // JSON olup olmadığını görmek için text al
        console.log("📡 API TEXT Yanıtı:", textData);

        try {
            const jsonData = JSON.parse(textData);  // JSON olarak parse etmeye çalış
            console.log("✅ JSON Olarak Parse Edildi:", jsonData);
            return jsonData.map(video => ({
                title: video.title,
                url: video.url,
                thumbnail: video.thumbnail || ""
            }));
        } catch (jsonError) {
            console.error("❌ API Yanıtı JSON değil, gelen veri:", textData);
            return [];
        }

    } catch (error) {
        console.error("❌ YouTube verileri çekilemedi:", error);
        return [];
    }
};











export const fetchInstagramData = async () => {
    const response = await fetch("API_URL_INSTAGRAM");
    const data = await response.json();
    return data.map(post => ({
        title: post.caption,
        url: post.permalink,
        thumbnail: post.media_url
    }));
};
