import React, { useEffect, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import axios from "axios";

const PlatformCard = ({ title, data, className }) => {
    return (
        <motion.div
            className={`platform-container ${className}`}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
        >
            <h2 className="text-2xl font-bold mb-4">{title}</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {data && data.length > 0 ? (
                    data.map((item, index) => (
                        <Card key={index} className="hover:shadow-lg transition-shadow duration-300">
                            <CardContent>
                                <a href={item.url} target="_blank" rel="noopener noreferrer" className="block">
                                    <img
                                        src={item.thumbnail || "/default-thumbnail.jpg"}
                                        alt={item.title}
                                        className="w-full h-40 object-cover rounded-lg"
                                    />
                                    <p className="mt-2 font-medium text-lg">{item.title}</p>
                                </a>
                            </CardContent>
                        </Card>
                    ))
                ) : (
                    <p className="text-center text-gray-300">Veri bulunamadı</p>
                )}
            </div>
        </motion.div>
    );
};

const TrendApp = () => {
    const [redditData, setRedditData] = useState([]);
    const [youtubeData, setYoutubeData] = useState([]);
    const [lastUpdated, setLastUpdated] = useState("");
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [redditResponse, youtubeResponse] = await Promise.all([
                    axios.get("http://localhost:8080/api/reddit/top"),
                    axios.get("http://localhost:8080/api/youtube/trends"),
                ]);
                setRedditData(redditResponse.data || []);
                setYoutubeData(youtubeResponse.data || []);
                setLastUpdated(new Date().toLocaleTimeString());
            } catch (error) {
                console.error("Veri çekme hatası:", error);
                setError("Veri alınırken bir hata oluştu. Lütfen daha sonra tekrar deneyin.");
            }
        };
        fetchData();
    }, []);

    return (
        <div className="min-h-screen bg-gray-900 text-white p-6">
            <header className="flex justify-between items-center mb-6">
                <h1 className="text-4xl font-bold">Trend App</h1>
                <span className="text-sm opacity-75">Son Güncelleme: {lastUpdated}</span>
            </header>
            {error ? (
                <p className="text-center text-red-500">{error}</p>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <PlatformCard title="Reddit" data={redditData} className="bg-orange-500" />
                    <PlatformCard title="YouTube" data={youtubeData} className="bg-red-600" />
                </div>
            )}
        </div>
    );
};

export default TrendApp;
