import React, { useEffect, useState } from "react";

const platforms = [
  { name: "Reddit", color: "#FF4500" },
  { name: "X", color: "#1DA1F2" },
  { name: "YouTube", color: "#FF0000" },
  { name: "Instagram", color: "#C13584" },
];

const App = () => {
  const [data, setData] = useState({});
  const [lastUpdated, setLastUpdated] = useState("");

  useEffect(() => {
    fetch("/api/getContent") // Backend API endpoint
        .then((res) => res.json())
        .then((result) => {
          setData(result);
          setLastUpdated(new Date().toLocaleTimeString());
        })
        .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
      <div style={{ padding: "20px", fontFamily: "Arial, sans-serif" }}>
        <div style={{ textAlign: "right", marginBottom: "10px", fontSize: "14px" }}>
          <strong>Last Updated:</strong> {lastUpdated}
        </div>
        {platforms.map((platform) => (
            <div key={platform.name} style={{ marginBottom: "20px" }}>
              <h2 style={{ color: platform.color }}>{platform.name}</h2>
              <div>
                {data[platform.name]?.map((item) => (
                    <div key={item.id} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
                      <img src={item.thumbnail} alt={item.title} style={{ width: "50px", height: "50px", marginRight: "10px" }} />
                      <a href={item.link} target="_blank" rel="noopener noreferrer" style={{ textDecoration: "none", color: "black" }}>
                        {item.title}
                      </a>
                    </div>
                ))}
              </div>
            </div>
        ))}
      </div>
  );
};

export default App;
